# Boolean
# When you compare two values, the expression is evaluated and Python returns the Boolean answer
print(10>9)

# The bool() function allows you to evaluate any value, and give you True or Fales in return
print(bool("Hello"))

x = "Hello"
y = 15

print(bool(x))
print(bool(y))

print(bool(0))

z = []
print(bool(z))